class FarhanError extends Error{constructor(t){super(t),this.name="FarhanError"}}class Farhan{constructor(){this.ascii_uppercase="ABCDEFGHIJKLMNOPQRSTUVWXYZ",this.ascii_lowercase="abcdefghijklmnopqrstuvwxyz",this.digits="0123456789",this.ascii_letters=this.ascii_uppercase+this.ascii_lowercase,this.ascii_letters_digits=this.ascii_letters+this.digits,this.base64_chars=this.ascii_letters_digits+"+/",this.hexdigits=this.digits+"abcdefABCDEF",this.octdigits="01234567"}ord(t){return t.charCodeAt(0)}chr(t){return String.fromCharCode(t)}bin(t,r=8){return t.toString(2).padStart(r,"0")}hex(t){return t.toString(16)}str2bin(t){this.validate(t);let r="",e=0;for(e=0;e<t.length;e++)r+=this.bin(this.chr(t.charAt(e)))+" ";return r}bin2str(t){this.validate(t);let r="",e=0,i=t.split(" ");for(e=0;e<i.length;e++)r+=this.chr(parseInt(i[e],2));return r}str2hex(t){this.validate(t);let r="",e=0;for(e=0;e<t.length;e++)r+=this.hex(this.ord(t.charAt(e)));return r}hex2str(t){this.validate(t);let r="",e=0;for(e=0;e<t.length;e+=2)r+=this.chr(parseInt(t.substr(e,2),16));return r}validate(t){if("string"!=typeof t)throw new FarhanError("A String type data is required");if(0===t.length)throw new FarhanError("String should not be empty")}base64encode(t){this.validate(t);let r=t.split(""),e=r.map(t=>this.bin(this.ord(t))),i=[],s=e.join("");for(;s.length%6!=0;)s+="0";for(let a=0;a<s.length;a+=6)i.push(s.substr(a,6).padEnd(6,"0"));let h=i.map(t=>"00"+t),n=h.map(t=>parseInt(t,2)),o=n.map(t=>this.base64_chars[t]);for(;o.length%4!=0;)o.push("=");return o.join("")}base64decode(t){if(this.validate(t),!/^([A-Za-z0-9+/]+)*(=)?$/.test(t))throw new FarhanError("Input String contains invalid Base64 characters or incorrect padding.");t=t.replace(/=/g,"");let r=t.split(""),e=r.map(t=>this.base64_chars.indexOf(t)).filter(t=>t>=0),i=e.map(t=>this.bin(t,6)),s=[],a=i.join("");for(;a.length%8!=0;)a+="0";for(let h=0;h<a.length;h+=8)s.push(a.substr(h,8));let n=s.map(t=>this.chr(parseInt(t,2)));return n.join("")}urlsafe_b64encode(t){return this.base64encode(t).replace("+","-").replace("/","_").replace("=",".")}urlsafe_b64decode(t){return this.base64decode(t).replace("-","+").replace("_","/").replace(".","=")}urlencode(t){return encodeURIComponent(t)}urldecode(t){return decodeURIComponent(t)}random_int(t,r){if(t>r)throw new FarhanError("max value should be greater then min value");return Math.floor(Math.random()*(r-t+1))+t}random_char(t,r){if((t=this.ord(t))>(r=this.ord(r)))throw new FarhanError("max value should be greater then min value");return this.chr(this.random_int(t,r))}random_choice(t){let r=this.random_int(0,t.length-1);return"object"==typeof t?t[r]:"string"==typeof t?t.charAt(r):void 0}random_hexstr(t=20){let r="",e=0;for(e=0;e<t;e++)r+=this.random_choice(this.hexdigits);return r}}
